(31, 63, 2, 'Slakoth', '[C] Amnesia :\r\nChoose 1 of the Defending Pokémon’s attacks. This Pokémon can’t use that attack during your opponent’s next turn.\r\n\r\n[C] Scratch : 10', NULL),
(31, 64, 2, 'Snorunt', '[C] Pebble Throw :\r\nChoose 1 of your opponent’s Benched Pokémon. This attack does 10 damage to that Pokémon. (Don’t apply Weakness and Resistance for Benched Pokémon.)\r\n\r\n[W][C] Double Headbutt : 20×\r\nFlip 2 coins. This attack does 20 damage times the number of heads.', NULL),
(31, 65, 2, 'Spheal', '[C] Defense Curl :\r\nFlip a coin. If heads, prevent all damage done to Spheal during your opponent’s next turn.\r\n\r\n[W][C] Aurora Beam : 20', NULL),
(31, 66, 2, 'Swablu', '[C] Fury Attack : 10×\r\nFlip 3 coins. This attack does 10 damage times the number of heads.', NULL),
(31, 67, 2, 'Torchic', '[R] Super Singe : 10\r\nFlip a coin. If heads, the Defending Pokémon is now Burned.', NULL),
